import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Square extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: "",
            name: "",
            count:0,
        }
    }
    render() {
        return (
            <div>
                <div>
            <button className="square">
                {this.props.value  }
            </button>
                </div>
                <p></p>
        <>{"\n" + this.props.name + " "}</>
                <>{"count: " + this.props.count}</>
            </div>

        );
    }
}

class ArrowBox extends React.Component {

    render () {
        return (
            <button>
                {this.props.value}
            </button>

        )
    }
}

class Board extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            squares: Array(2).fill(null),
        }
    }

    handleClick(i) {
        const squares = this.state.squares.slice();
        squares[i] = '\uD83C\uDF50';
        this.setState({squares: squares});
    }

    renderSquare(i,k,j) {
        return <Square value={i} name={k} count = {j}/>;
    }

    renderArrow(i) {
        return <ArrowBox value={i} />;
    }
    //className="board-row"  {this.renderSquare(1)}
    render() {
        const status = 'Next player: X';

        return (
            <div>
                <div className="status">{status}</div>
                <div >
                    {this.renderArrow("\u2B05\uFE0F")}
                    {this.renderArrow("\u27A1\uFE0F")}
                    {this.renderSquare('\uD83C\uDF50',"pear",0)}

                    {this.renderSquare("\uD83C\uDF4E","apple",0)}

                </div>

            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
                <div className="game-info">
                    <div>{/* status */}</div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
