import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Square extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: "",
            name: "",
        }
    }
    render() {
        return (
            <div>
            <button className="square">
                {this.props.value}
            </button>
        <>{this.props.name}</>
            </div>
        );
    }
}

class ArrowBox extends React.Component {

    render () {
        return (
            <button>
                {this.props.value}
            </button>

        )
    }
}

class Board extends React.Component {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         squares: Array(1).fill(null),
    //     }
    // }

    handleClick(i) {
        const squares = this.state.squares.slice();
        squares[i] = '\uD83C\uDF50';
        this.setState({squares: squares});
    }

    renderSquare(i) {
        return <Square value={i} />;
    }

    renderArrow(i) {
        return <ArrowBox value={i} />;
    }
    //className="board-row"  {this.renderSquare(1)}
    render() {
        const status = 'Next player: X';

        return (
            <div>
                <div className="status">{status}</div>
                <div >
                    {this.renderArrow("\u2B05\uFE0F")}
                    {this.renderArrow("\u27A1\uFE0F")}
                    {this.renderSquare('\uD83C\uDF50')}

                    {this.renderSquare("\uD83C\uDF4E")}

                </div>

            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
                <div className="game-info">
                    <div>{/* status */}</div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
